	0 (>L:BTN_FS_LMPD_KEY1_EXEC, Bool)

	(L:MPCD-menu, Enum) 0 == if{
		1000 (>L:MPCD-menu, Enum)
		quit
	}
	(L:MPCD-menu, Enum) 1000 div 1 == if{
		(L:GUN_FIRE_RATE, Number) 0 ==
			if{
				1 (>L:GUN_FIRE_RATE, Number)
			}
			els{
				0 (>L:GUN_FIRE_RATE, Number)
			}
		quit
	}
	(L:MPCD-menu, Enum) 1000 div 2 == if{
			(L:BIT_PAGES) 1 ==
				if{
					1 (>L:BIT_FUNC_PAGES)
				}
				els{
					1 (>L:BIT_PAGES)
				}
		quit
	}
	(L:MPCD-menu, Enum) 1000 div 15 == if{
		(L:MPCD_DTM_DAT) 0 ==
			if{
				1 (>L:MPCD_DTM_DAT)
			}
			els{
				0 (>L:MPCD_DTM_DAT)
			}
		quit
	}
}

